package week1ass1;

public class pb6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5; 
		while(i==5)
		{System.out.println("condition complete for while");
		break;}
		
		do {
			System.out.println("condition complete for do while");
		}while(i<4);

	}

}
